    - dm11x*m2^2 - dm22x*m1^2 + 2*dm1x*m2*m12 + 2*dm2x*m1*m12 - 2*dm2x*m2*m11 + 2*dm12x*m1*m2 - 2*dm1x*m1*m22))/...
    (- m22*m1^2 + 2*m12*m1*m2 - m11*m2^2 + m0*(m11*m22 - m12^2))^2 - (m1*d2m12xx - m2*d2m11xx - m11*d2m2xx + m12*d2m1xx +...
    2*dm1x*dm12x - 2*dm2x*dm11x)/(m0*(m11*m22 - m12^2) - m2^2*m11 - m1^2*m22 + 2*m1*m2*m12) - (2*(m1*m12 - m2*m11)*...
    (m0*(dm11x*m22 - 2*dm12x*m12 + dm22x*m11) + dm0x*(m11*m22 - m12^2) - dm11x*m2^2 - dm22x*m1^2 + 2*dm1x*m2*m12 +...
    2*dm2x*m1*m12 - 2*dm2x*m2*m11 + 2*dm12x*m1*m2 - 2*dm1x*m1*m22)^2)/(- m22*m1^2 + 2*m12*m1*m2 - m11*m2^2 + m0*...
    (m11*m22 - m12^2))^3 + ((m1*m12 - m2*m11)*(m0*(m11*d2m22xx - 2*m12*d2m12xx + m22*d2m11xx - 2*dm12x^2 + 2*dm11x*...
    dm22x) - 2*dm2x^2*m11 - 2*dm1x^2*m22 + (m11*m22 - m12^2)*d2m0xx - m2^2*d2m11xx - m1^2*d2m22xx + 2*dm0x*(dm11x*m22 -...
    2*dm12x*m12 + dm22x*m11) + 4*dm1x*dm2x*m12 + 4*dm1x*dm12x*m2 - 4*dm2x*dm11x*m2 + 4*dm2x*dm12x*m1 - 4*dm1x*dm22x*m1 +...
    2*m1*m2*d2m12xx + 2*m1*m12*d2m2xx - 2*m2*m11*d2m2xx + 2*m2*m12*d2m1xx - 2*m1*m22*d2m1xx))/(- m22*m1^2 + 2*m12*m1*m2 - m11*m2^2 + m0*(m11*m22 - m12^2))^2;
end

